    tr[k] = Node(l, r);

    if (tr[k].left == tr[k].right) &#123;
      tr[k].val.max = -pos[l - 1];
      tr[k].val.index = l;
      return void();
    &#125;

    int mid = get_mid(k);
    int lc = get_lc(k), rc = get_rc(k);

    build_tree(lc, l, mid);
    build_tree(rc, mid + 1, r);

    push_up(k);
  &#125;

  void modify(int k, int l, int r, int val) &#123;
