      modify(lc, l, r, val);
    &#125; else if (l &gt; mid) &#123;
      modify(rc, l, r, val);
    &#125; else &#123;
      modify(lc, l, mid, val);
      modify(rc, mid + 1, r, val);
    &#125;

    push_up(k);
  &#125;

  pair&lt;long long, long long&gt; query(int k, int l, int r) &#123;
    if (tr[k].left &gt;= l &amp;&amp; tr[k].right &lt;= r) &#123;
      return make_pair(tr[k].val.max, tr[k].val.index);
    &#125;

    push_down(k);

    int mid = get_mid(k);
    int lc = get_lc(k), rc = get_rc(k);

