        cin &gt;&gt; father[i];
        add_edge(i, father[i]);
        add_edge(father[i], i);
    &#125;

    dfs(1);

    while (q--) &#123;
        cin &gt;&gt; query_node;
        cout &lt;&lt; answer[query_node] &lt;&lt; &#x27;\n&#x27;;
