    for (int i = 1; i &lt;= m; i++) &#123;
        cin &gt;&gt; op;

        if (op == 1) &#123;
            cin &gt;&gt; x &gt;&gt; y;

            if (erase[x] || erase[y]) &#123;
                continue;
            &#125;

            x = tr.find(x), y = tr.find(y);
            if (x == y) &#123;
                continue;
            &#125;

            tr.father[x] = tr.father[y] = tr.merge(x, y);
        &#125; else if (op == 2) &#123;
            cin &gt;&gt; x;

            if (erase[x] == true) &#123;
                cout &lt;&lt; &quot;-1\n&quot;;
            &#125; else &#123;
