        name x = 0, f = 1; char ch = getchar();
        while (!isdigit(ch)) &#123; if (ch == &#x27;-&#x27;) f = -1; ch = getchar(); &#125;
        while (isdigit(ch)) &#123; x = (x &lt;&lt; 1) + (x &lt;&lt; 3) + (ch ^ 48); ch = getchar(); &#125;
        return x * f;
    &#125;
    template&lt;typename name&gt; void _write(name x) &#123;
        if (x &gt; 9) _write(x / 10);
        putchar(x % 10 + &#x27;0&#x27;);
    &#125;
    template&lt;typename name&gt; void write(name x) &#123;
        if (x &lt; 0) putchar(&#x27;-&#x27;), x = -x;
        _write(x);
    &#125;
&#125;

constexpr int N = 1e5 + 5, K = 105, MOD = 1e9 + 7;
int head[N], next[N &lt;&lt; 1], to[N &lt;&lt; 1], cnt = 0;
int dp[N][K][2][2], size[N], tmp[K][2][2];
int n, k, u, v;

inline void AddEdge(const int u, const int v) &#123;
    to[cnt] = v;
    next[cnt] = head[u];
    head[u] = cnt++;
&#125;

inline void Dfs(int u, int father) &#123;
    dp[u][0][0][0] = dp[u][1][1][0] = 1;
    size[u] = 1;

    for (int i = head[u]; ~i; i = next[i]) &#123;
        const int v = to[i];
        if (v == father) continue;

        Dfs(v, u);
        size[u] += size[v];

        // ! 这里要进行备份，不然转移的时候会出现问题（用已修改的状态修改未修改的状态）
        for (int t = 0; t &lt;= std::min(k, size[u]); t++) &#123;
            tmp[t][0][0] = dp[u][t][0][0];
            tmp[t][1][0] = dp[u][t][1][0];
            tmp[t][0][1] = dp[u][t][0][1];
            tmp[t][1][1] = dp[u][t][1][1];
            dp[u][t][0][0] = dp[u][t][1][0] = dp[u][t][0][1] = dp[u][t][1][1] = 0;
        &#125;

        // ! 注意枚举范围，不能直接枚举区间 [0,k]，不然会 TLE
        for (int t = 0; t &lt;= std::min(size[u], k); t++) &#123;
            // ! 这里同理
            for (int j = 0; j &lt;= std::min(size[v], t); j++) &#123;
                if (t - j &gt; size[u] - size[v]) continue;

                // // 可恶的转移
                (dp[u][t][0][0] += (long long) tmp[t - j][0][0] * dp[v][j][0][1] % MOD) %= MOD;
                (dp[u][t][0][1] += ((long long) tmp[t - j][0][1] * ((dp[v][j][0][1] + dp[v][j][1][1]) % MOD) % MOD + (long long) tmp[t - j][0][0] * dp[v][j][1][1] % MOD) % MOD) %= MOD;
                (dp[u][t][1][0] += (long long) tmp[t - j][1][0] * ((dp[v][j][0][1] + dp[v][j][0][0]) % MOD) % MOD) %= MOD;
                (dp[u][t][1][1] += ((long long) (tmp[t - j][1][1] * (((long long) dp[v][j][0][1] + dp[v][j][0][0] + dp[v][j][1][0] + dp[v][j][1][1]) % MOD) % MOD) + (long long) tmp[t - j][1][0] * ((dp[v][j][1][1] + dp[v][j][1][0]) % MOD) % MOD) % MOD) %= MOD;
            &#125;
        &#125;
    &#125;
&#125;

int main() &#123;
    using namespace IO;

    n = read&lt;int&gt;();
    k = read&lt;int&gt;();

    for (int i = 1; i &lt;= n; i++) head[i] = -1;

    for (int i = 1; i &lt; n; i++) &#123;
        u = read&lt;int&gt;(), v = read&lt;int&gt;();
        AddEdge(u, v);
        AddEdge(v, u);
    &#125;

    Dfs(1, 1);

    write((1ll * dp[1][k][0][1] + dp[1][k][1][1]) % MOD);

    return 0;
&#125;
 洛谷-P1352 没有上司的舞会 (经典题)
 题目大意
 题目大意
给定一颗有 nnn 个节点的树，表示员工的架构，现在有一个舞会需要举办，你要选取一些人参加，第 i\large ii 个人参加可以为舞会带来 ri\large r_iri​ 的快乐值，但是如果第 i\large ii 个人的直接上司 (i\large ii 的父亲节点) 参加了，那么 i\large ii 就不会参加，你要最大化选择的人的快乐值之和。
数据范围：n≤6×103\large n \le 6 \times 10^3n≤6×103。

 思路
定义状态 fu,0/1\large f_{u,0/1}fu,0/1​ 表示节点 u\large uu 参加/不参加能为舞会带来的最大价值。
转移如下：
fu,0=∑v∈son⁡(u)max⁡(fv,0,fv,1)fu,1=∑v∈son⁡(u)fv,0+ri\large 
\begin{aligned}
f_{u,0} &amp;= \sum_{v \in \operatorname{son}(u)} \max(f_{v,0}, f_{v,1}) \\
f_{u,1} &amp;= \sum_{v \in \operatorname{son}(u)} f_{v,0} + r_i
\end{aligned}
fu,0​fu,1​​=v∈son(u)∑​max(fv,0​,fv,1​)=v∈son(u)∑​fv,0​+ri​​
注意我的树存的是双向边，和边有关的数组要开 2\large 22 倍。
 Code
Code
#include &lt;bits/stdc++.h&gt;

using std::cin;
using std::cout;

constexpr int MAXN = 6e4 + 5;
int head[MAXN], to[MAXN], next[MAXN], cnt = 0;
int n, r[MAXN], u, v;
int dp[MAXN][2];

void add_edge(const int u, const int v) &#123;
    to[cnt] = v;
    next[cnt] = head[u];
    head[u] = cnt++;
&#125;

void dfs(const int u, const int father) &#123;
    dp[u][1] = r[u];

    for (int i = head[u]; ~i; i = next[i]) &#123;
        const int v = to[i];
        if (v == father) continue;

        dfs(v, u);
        dp[u][0] += std::max(dp[v][0], dp[v][1]);
        dp[u][1] += dp[v][0];
    &#125;
&#125;

int main() &#123;
    std::ios::sync_with_stdio(false);
    cin.tie(nullptr);
    cout.tie(nullptr);

    std::memset(head, -1, sizeof(head));

    cin &gt;&gt; n;

    for (int i = 1; i &lt;= n; i++) &#123;
        cin &gt;&gt; r[i];
    &#125;

    for (int i = 1; i &lt; n; i++) &#123;
        cin &gt;&gt; u &gt;&gt; v;
        add_edge(u, v);
        add_edge(v, u);
    &#125;

    dfs(1, 1);

    cout &lt;&lt; std::max(dp[1][0], dp[1][1]) &lt;&lt; &#x27;\n&#x27;;

    return 0;
&#125;
 洛谷-P3478 消防局的 STA-Station
 题目大意
 P3478 [POI 2008] STA-Station 题目大意
给定一个 n\large nn 个点的树，请求出一个结点，使得以这个结点为根时，所有结点的深度之和最大。
一个结点的深度之定义为该节点到根的简单路径上边的数量。
数据范围：n≤106\large n \le 10^6n≤106.

 思路
换根 DP。设 fu\large f_ufu​ 表示以 u\large uu 为根节点的时候所有节点的深度之和是多少。换根一般都有两个 dfs\large dfsdfs，我们在第一遍 dfs\large dfsdfs 的时候记录节点的深度 dep\large depdep，子树大小 size\large sizesize，然后 fu\large f_ufu​ 的状态为：fu=depu\large f_u = dep_ufu​=depu​。
在第二遍 dfsdfsdfs 时，我们自上而下更新 fvf_vfv​ 的值，转移为：
fv=fu−sizev+n−sizev\large 
f_v = f_u - size_v + n - size_v
fv​=fu​−sizev​+n−sizev​
即子树 vvv 内所有点的深度减一，子树外的所有深度加一。
注意 long long!!!
 Code
Code
#include &lt;bits/stdc++.h&gt;

using std::cin;
using std::cout;

constexpr int N = 1e6 + 5;
int head[N], next[N &lt;&lt; 1], to[N &lt;&lt; 1], cnt = 0;
int n, u, v, dp[N], dep[N], size[N];

void AddEdge(const int u, const int v) &#123;
    to[cnt] = v;
    next[cnt] = head[u];
    head[u] = cnt++;
&#125;

void Dfs1(const int u, const int father) &#123;
    dp[u] += dep[u];
    dep[u] = dep[father] + 1;
    size[N] = 1;

    for (register int i = head[u]; ~i; i = next[i]) &#123;
        register const int v = to[i];
        if (v == father) continue;

        Dfs1(v, u);
        size[u] += size[v];
    &#125;
&#125;

void Dfs2(const int u, const int father) &#123;
    for (register int i = head[u]; ~i; i = next[i]) &#123;
        register const int v = to[i];
        if (v == father) continue;

        dp[v] = dp[u] - size[v] + n - size[v];
        Dfs2(v, u);
    &#125;
&#125;



int main() &#123;
    std::ios::sync_with_stdio(false);
    cin.tie(nullptr);
    cout.tie(nullptr);

    std::memset(head, -1, sizeof(head));

    cin &gt;&gt; n;

    for (int i = 1; i &lt; n; i++) &#123;
        cin &gt;&gt; u &gt;&gt; v;

        AddEdge(u, v);
        AddEdge(v, u);
    &#125;

    Dfs1(1, 1);
    Dfs2(1, 1);

    int ans = 1;
    for (int i = 2; i &lt;= n; i++) &#123;
        if (dp[i] &gt; dp[ans]) ans = i;
    &#125;

    cout &lt;&lt; ans &lt;&lt; &#x27;\n&#x27;;

    return 0;
&#125;
